app.filter('imgByName', function(){
    return function(input, name) {
        
        var imgUrl = '';
        
        if (input) {
            $.each(input, function(i, obj) {
                if (obj.nome === name) {
                    imgUrl = obj.url;
                    return false;
                }
            });

            if (imgUrl === '') {
                return 'placeholder';
            }
            return imgUrl;
        }
        return null
    };
});
